// components/ReviewsList.js

import React, { useEffect, useState } from 'react';
import '../App.css';

function ReviewsList() {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/api/reviews') // Убедитесь, что URL правильный
      .then(response => response.json())
      .then(data => setReviews(data));
  }, []);

  return (
    <div className="reviews-list">
      <h2>Reviews</h2>
      <div className="list">
        {reviews.map(review => (
          <div key={review.id} className="review-card">
            <h3>Rating: {review.rating}/5</h3>
            <p>Hotel ID: {review.hotel_id}</p>
            <p>Customer ID: {review.customer_id}</p>
            <p>Comment: {review.comment}</p>
            <p>Date: {new Date(review.review_date).toLocaleDateString()}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ReviewsList;
