// frontend/src/components/BookingsList.js

import React, { useEffect, useState } from 'react';
import '../App.css';

function BookingsList() {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/api/booking') // Убедитесь, что URL правильный
      .then(response => response.json())
      .then(data => setBookings(data))
      .catch(error => console.error('Error fetching bookings:', error));
  }, []);

  return (
    <div className="bookings-list">
      <h2>Bookings</h2>
      <div className="list">
        {bookings.map(booking => (
          <div key={booking.id} className="booking-card">
            <h3>Booking ID: {booking.id}</h3>
            <p>Hotel ID: {booking.hotel_id}</p>
            <p>Room ID: {booking.room_id}</p>
            <p>Customer ID: {booking.customer_id}</p>
            <p>Check-in: {new Date(booking.check_in_date).toLocaleDateString()}</p>
            <p>Check-out: {new Date(booking.check_out_date).toLocaleDateString()}</p>
            <p>Total Price: ${booking.total_price}</p>
            <p>Status: {booking.cancelled ? 'Cancelled' : 'Active'}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default BookingsList;
